import React, { useEffect, useState } from "react";
import "./App.css";
import SplitFlapDisplay from "./components/SplitFlapDisplay";

// Replace 'YOUR_API_KEY' with your actual Alpha Vantage API key
const ALPHA_VANTAGE_API_KEY = "YOUR_API_KEY";

const SYMBOLS = {
  crypto: ["bitcoin", "cardano"], // CoinGecko IDs
  stocks: ["CIFR", "MSTR", "TSLA", "FBTC", "SPX", "DJI"], // Stock symbols
};

const DISPLAY_NAMES = {
  bitcoin: "BTC",
  cardano: "ADA",
};

const REFRESH_INTERVAL = 15000; // 15 seconds to avoid rate limits

function App() {
  const [prices, setPrices] = useState({});
  const [changes, setChanges] = useState({});

  const fetchCryptoPrices = async () => {
    try {
      const response = await fetch(
        `https://api.coingecko.com/api/v3/simple/price?ids=${SYMBOLS.crypto.join(
          ","
        )}&vs_currencies=usd`
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const cryptoPrices = {};
      SYMBOLS.crypto.forEach((symbol) => {
        if (data[symbol]) {
          cryptoPrices[symbol.toUpperCase()] = data[symbol].usd;
        }
      });

      return cryptoPrices;
    } catch (error) {
      console.error("Error fetching crypto prices:", error);
      return {};
    }
  };

  const fetchStockPrices = async () => {
    try {
      const stockPrices = {};
      const stockChanges = {};
      for (const symbol of SYMBOLS.stocks) {
        const response = await fetch(
          `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${symbol}&apikey=${ALPHA_VANTAGE_API_KEY}`
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        const price = parseFloat(data["Global Quote"]["05. price"]);
        const changePercent = parseFloat(
          data["Global Quote"]["10. change percent"]
        );
        stockPrices[symbol] = price;
        stockChanges[symbol] = changePercent;
      }

      setChanges(stockChanges);
      return stockPrices;
    } catch (error) {
      console.error("Error fetching stock prices:", error);
      return {};
    }
  };

  const fetchAllPrices = async () => {
    const [cryptoPrices, stockPrices] = await Promise.all([
      fetchCryptoPrices(),
      fetchStockPrices(),
    ]);

    setPrices({ ...cryptoPrices, ...stockPrices });
  };

  useEffect(() => {
    fetchAllPrices();
    const interval = setInterval(fetchAllPrices, REFRESH_INTERVAL);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <div className="ticker-container">
        {[...SYMBOLS.crypto, ...SYMBOLS.stocks].map((symbol) => (
          <SplitFlapDisplay
            key={symbol}
            symbol={DISPLAY_NAMES[symbol] || symbol.toUpperCase()}
            price={prices[symbol.toUpperCase()]?.toFixed(2) || "0.00"}
            change={changes[symbol]}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
