import React, { useEffect, useState } from "react";
import "./SplitFlapDisplay.css";

const CHARS = "0123456789.,ABCDEFGHIJKLMNOPQRSTUVWXYZ $%-";

const SplitFlapCell = ({ target, duration = 50 }) => {
  const [current, setCurrent] = useState(target);
  const [flipping, setFlipping] = useState(false);

  useEffect(() => {
    if (target === current) return;

    setFlipping(true);
    let currentIndex = CHARS.indexOf(current);

    const interval = setInterval(() => {
      currentIndex = (currentIndex + 1) % CHARS.length;
      setCurrent(CHARS[currentIndex]);

      if (CHARS[currentIndex] === target) {
        clearInterval(interval);
        setFlipping(false);
      }
    }, duration);

    return () => clearInterval(interval);
  }, [target, current, duration]);

  return (
    <div className={`split-flap-cell ${flipping ? "flipping" : ""}`}>
      {current}
    </div>
  );
};

const SplitFlapDisplay = ({ symbol, price, change }) => {
  const changeColor = change > 0 ? "#009a44" : "#da291c";
  const changeSign = change < 0 ? "-" : " ";
  const changeText = `${Math.abs(change).toFixed(1)}%`;
  const priceText = `$${Math.ceil(price).toLocaleString()}`;

  // Ensure the ticker symbol is at least 4 characters long
  const paddedSymbol = symbol.padEnd(4, " ");
  const paddedChangeText = changeText.padEnd(6, " "); // Assuming 6 cells for change
  const paddedPriceText = priceText.padStart(8, " "); // Assuming 8 cells for price

  return (
    <div className="split-flap-display">
      {/* Ticker Symbol */}
      {paddedSymbol.split("").map((char, index) => (
        <SplitFlapCell key={`symbol-${index}`} target={char.toUpperCase()} />
      ))}

      {/* Change Indicator */}
      <div
        className="split-flap-cell"
        style={{
          backgroundColor: changeColor,
        }}
      />

      {/* Change Sign */}
      <SplitFlapCell target={changeSign} />

      {/* Percentage Change */}
      {paddedChangeText.split("").map((char, index) => (
        <SplitFlapCell key={`change-${index}`} target={char.toUpperCase()} />
      ))}

      {/* Current Price */}
      <div className="split-flap-price">
        {paddedPriceText.split("").map((char, index) => (
          <SplitFlapCell key={`price-${index}`} target={char.toUpperCase()} />
        ))}
      </div>
    </div>
  );
};

export default SplitFlapDisplay;
